static uint8_t getAscii(char letter)
{
    if(letter == 'a' || letter == 'A') return 10;
    else if(letter == 'b' || letter == 'B') return 11;
    else if(letter == 'c' || letter == 'C') return 12;
    else if(letter == 'd' || letter == 'D') return 13;
    else if(letter == 'e' || letter == 'E') return 14;
    else if(letter == 'f' || letter == 'F') return 15;
    else if(letter == 'g' || letter == 'G') return 16;
    else if(letter == 'h' || letter == 'H') return 17;
    else if(letter == 'i' || letter == 'I') return 18;
    else if(letter == 'j' || letter == 'J') return 19;
    else if(letter == 'k' || letter == 'K') return 20;
    else if(letter == 'l' || letter == 'L') return 21;
    else if(letter == 'n' || letter == 'N') return 22;
    else if(letter == 'o' || letter == 'O') return 23;
    else if(letter == 'p' || letter == 'P') return 24;
    else if(letter == 'q' || letter == 'Q') return 25;
    else if(letter == 'r' || letter == 'R') return 26;
    else if(letter == 's' || letter == 'S') return 27;
    else if(letter == 't' || letter == 'T') return 28;
    else if(letter == 'u' || letter == 'U') return 29;
    else if(letter == 'v' || letter == 'V') return 30;
    else if(letter == 'x' || letter == 'X') return 31;
    else if(letter == 'y' || letter == 'Y') return 32;
    else if(letter == 'z' || letter == 'Z') return 33;
    else if(letter == ' ') return 34;
    else if(letter == '>') return 35;
    else if(letter == '<') return 36;
}

void outString(char *string, uint16_t Xpos, uint16_t Ypos, uint32_t charColor)
{
    char temp;
    uint8_t ascii;
    while(*string != 0)     //while not the null character
    {
        temp = *string;
        ascii = getAscii(temp);
        if(ascii == 34)
        {
            Xpos += 1;
        }
        else
        {
            LM_Text(Xpos, Ypos, ascii, charColor);
            Xpos += 4;
        }
        *string++;
    }
}

void LM_Text(uint16_t Xpos, uint16_t Ypos, uint8_t ASCI, uint32_t charColor)
{
    uint8_t* tempAscii;
    switch(ASCI){

    case 0:
        tempAscii = Zero;
        break;
    case 1:
        tempAscii = One;
        break;
    case 2:
        tempAscii = Two;
        break;
    case 3:
        tempAscii = Three;
        break;
    case 4:
        tempAscii = Four;
        break;
    case 5:
        tempAscii = Five;
        break;
    case 6:
        tempAscii = Six;
        break;
    case 7:
        tempAscii = Seven;
        break;
    case 8:
        tempAscii = Eight;
        break;
    case 9:
        tempAscii = Nine;
        break;
    case 10:
        tempAscii = letterA;
        break;
    case 11:
        tempAscii = letterB;
        break;
    case 12:
        tempAscii = letterC;
        break;
    case 13:
        tempAscii = letterD;
        break;
    case 14:
        tempAscii = letterE;
        break;
    case 15:
        tempAscii = letterF;
        break;
    case 16:
        tempAscii = letterG;
        break;
    case 17:
        tempAscii = letterH;
        break;
    case 18:
        tempAscii = letterI;
        break;
    case 19:
        tempAscii = letterJ;
        break;
    case 20:
        tempAscii = letterK;
        break;
    case 21:
        tempAscii = letterL;
        break;
    case 22:
        tempAscii = letterN;
        break;
    case 23:
        tempAscii = letterO;
        break;
    case 24:
        tempAscii = letterP;
        break;
    case 25:
        tempAscii = letterQ;
        break;
    case 26:
        tempAscii = letterR;
        break;
    case 27:
        tempAscii = letterS;
        break;
    case 28:
        tempAscii = letterT;
        break;
    case 29:
        tempAscii = letterU;
        break;
    case 30:
        tempAscii = letterV;
        break;
    case 31:
        tempAscii = letterX;
        break;
    case 32:
        tempAscii = letterY;
        break;
    case 33:
        tempAscii = letterZ;
        break;
    case 34:
        tempAscii = blankSpace;
        break;
    case 35:
        tempAscii = menuPointer;
        break;
    case 36:
        tempAscii = menuPointerInverted;
        break;
    }

    int Index = 0;

    for (int i = Xpos; i < Xpos+3; i++)
    {
        int shift = 2;
        for (int j = Ypos ; j< Ypos+7; j++)
        {
            if (tempAscii[Index]>> (7 - shift) & 0x01 == 0x01 )
            {
                bufferBlue[i+(j*64)] = charColor  & 0x7F;
                bufferGreen[i+(j*64)] =( charColor >> 8 ) & 0x7F;
                bufferRed[i+(j*64)] =(charColor >> 16 ) &0x7F;
            }
            shift++;
        }

        Index++;
    }
}

void out_image(uint32_t* image, uint8_t Xpos, uint8_t Ypos, uint8_t whidth, uint8_t hight)
{
    int Index = 0;
    for (int i = Xpos; i < Xpos+whidth; i++)
    {
        for (int j = Ypos ; j< Ypos+hight; j++)
        {
            bufferBlue[j+(i*64)] = image[Index]  & 0x7F;
            bufferGreen[j+(i*64)] =( image[Index] >> 8 ) & 0x7F;
            bufferRed[j+(i*64)] =(image[Index] >> 16 ) &0x7F;
            Index++;
        }
    }
}

