void enemies_updater(){

    uint8_t direction = 0;
    uint8_t counter = 0;
    uint8_t timetoshoot = 0;

    while (1){

        if (life_bugs == 0){

            G8RTOS_KillSelf();
            while(1);
        }

        if (direction == 0 ) {

            if (counter < 10){
                counter++;
                for (int i = 0; i < numOfBugs ; i++){
                    //   if(bugs_table[i].inTheTable == 1)
                    bugs_table[i].distination_y--;
                    bugs_table[i].predistination_y--;



                }

                //                if (bugs_table[1].distination_y - bugs_table[0].distination_y  < 8 ) bugs_table[1].distination_y  = bugs_table[0].distination_y + 8;
                //                if (bugs_table[2].distination_y - bugs_table[1].distination_y  < 8 ) bugs_table[2].distination_y  = bugs_table[1].distination_y + 8;
                //                if (bugs_table[4].distination_y - bugs_table[3].distination_y  < 8 ) bugs_table[4].distination_y  = bugs_table[3].distination_y + 8;

            } else
            {
                if(timetoshoot == 5) timetoshoot = 0;
                else timetoshoot++;
                counter = 0;
                direction = 1;
            }
        }
        else
        {
            if (counter < 10){
                counter++;
                for (int i = 0; i < numOfBugs ; i++){
                    //  if(bugs_table[i].inTheTable == 1)
                    bugs_table[i].distination_y++;
                    bugs_table[i].predistination_y++;

                }

                //                            if (bugs_table[1].distination_y - bugs_table[0].distination_y  > 8 ) bugs_table[1].distination_y  = bugs_table[0].distination_y + 8;
                //                            if (bugs_table[2].distination_y - bugs_table[1].distination_y  > 8 ) bugs_table[2].distination_y  = bugs_table[1].distination_y + 8;
                //                            if (bugs_table[4].distination_y - bugs_table[3].distination_y  > 8 ) bugs_table[4].distination_y  = bugs_table[3].distination_y + 8;

            } else
            {
                counter = 0;
                direction = 0;
            }

        }

        if(timetoshoot == 3)
        {
            for(int i = 0; i < numOfBugs; i++)
            {
                if(bugs_table[i].alive)
                {
                    bugs_table[i].isShooting = 1;
                    bugs_table[i].distination_y = galagaShips_starX;
                    bugs_table[i].distination_x = galagaShips_starY;
                    G8RTOS_AddThread(&bug_bullet, "bug_bullet", 1);
                    break;
                }
            }
            timetoshoot++;
        }
        G8RTOS_OS_Sleep(100);
    }
}

void menuListener()
{
    uint8_t temp;
    menuItem = 0;

    while(1)
    {
        temp = RXDataL;
        if((temp & 0x08) == 0x08)
        {
            if(menuItem == 4)
            {
                menuItem = 0;
            }
            else
            {
                menuItem++;
            }
            G8RTOS_OS_Sleep(250);
        }
        else if((temp & 0x04) == 0x04)
        {
            if(menuItem == 0)
            {
                menuItem = 4;
            }
            else
            {
                menuItem--;
            }
            G8RTOS_OS_Sleep(250);
        }
        G8RTOS_OS_Sleep(50);
    }
}

